#include "IAfterSalesService.h"int main() {	freopen("/dev/console","w",stdout);//stdout重定向到串口	sp <IServiceManager> sm = defaultServiceManager(); //获取service manager引用	int status = sm->addService(String16("service.aftersale"), new BnAfterSalesService()); //注册"service.aftersale"服务到service manager	printf("addService service.aftersale %d\n",status);//	fflush(stdout);	ProcessState::self()->startThreadPool(); //启动线程池	IPCThreadState::self()->joinThreadPool(); //把主线程加入线程池	return 0;}